mapper.select:
SELECT menu_id , menu_nm , menu_depth1 FROM siana.menu WHERE 1 = 1 AND menu_depth1 = #{depth1Id}
AND menu_depth2 = #{depth2Id} AND menu_depth3 = 0
AND menu_depth2 = 0

mapper.insert:
INSERT INTO
     siana.menu (
     menu_nm
   , menu_desc
   , menu_depth1
   , menu_depth2
   , menu_depth3
   , menu_order
   , menu_url
   , menu_param
   , menu_pop_yn
   , menu_use_yn
 ) VALUES (
    #{menuNm}
    , #{menuDesc}
    , #{menuDepth1}
    , #{menuDepth2}
    , #{menuDepth3}
    , #{menuOrder}
    , #{menuUrl}
    , #{menuParam}
    , #{menuPopYn}
    , #{menuUseYn}
 )

mapper.update:
UPDATE
    siana.menu
SET
    menu_nm = #{menuNm}
    , menu_desc = #{menuDesc}
    , menu_depth1 = #{menuDepth1}
    , menu_depth2 = #{menuDepth2}
    , menu_depth3 = #{menuDepth3}
    , menu_order = #{menuOrder}
    , menu_url = #{menuUrl}
    , menu_param = #{menuParam}
    , menu_pop_yn = #{menuPopYn}
    , menu_use_yn = #{menuUseYn}
WHERE 1=1
AND menu_id = #{menuId}
